use validator::ValidationError;

pub fn validate_despeckle_level(level: f64) -> Result<(), ValidationError> {
    (level == 0f64 || (level >= 1f64 && level <= 3f64))
        .then_some(())
        .ok_or(ValidationError::new("invalid value for despeckle_level"))
}

